package fr.acinq.bitcoin

import java.io.{ByteArrayInputStream, InputStream}

import org.scalatest.FlatSpec
import org.junit.runner.RunWith
import org.scalatest.junit.JUnitRunner

@RunWith(classOf[JUnitRunner])
class PSBTSpec extends FlatSpec{

  "PSBT utils" should "deserialize a PSBT" in {

    //PSBT with one P2PKH input which has a non-final scriptSig.
    val rawPsbt = ""

    val psbt = PSBT.read(new ByteArrayInputStream(BinaryData(rawPsbt)))

    assert(psbt.inputs.size == 1)
  }


}
