package fr.acinq.bitcoinscala

import org.scalatest.FunSuite
import scodec.bits._

class LexicographicalOrderingSpec extends FunSuite {
  test("reference test #1") {
    val tx = Transaction.read("")
    assert(tx.txid == ByteVector32(hex"0a6a357e2f7796444e02638749d9611c008b253fb55f5dc88b739b230ed0c4c3"))

    val tx1 = LexicographicalOrdering.sort(tx)
    val expected = Seq(
      hex"0e53ec5dfb2cb8a71fec32dc9a634a35b7e24799295ddd5278217822e0b31f57",
      hex"26aa6e6d8b9e49bb0630aac301db6757c02e3619feb4ee0eea81eb1672947024",
      hex"28e0fdd185542f2c6ea19030b0796051e7772b6026dd5ddccd7a2f93b73e6fc2",
      hex"381de9b9ae1a94d9c17f6a08ef9d341a5ce29e2e60c36a52d333ff6203e58d5d",
      hex"3b8b2f8efceb60ba78ca8bba206a137f14cb5ea4035e761ee204302d46b98de2",
      hex"402b2c02411720bf409eff60d05adad684f135838962823f3614cc657dd7bc0a",
      hex"54ffff182965ed0957dba1239c27164ace5a73c9b62a660c74b7b7f15ff61e7a",
      hex"643e5f4e66373a57251fb173151e838ccd27d279aca882997e005016bb53d5aa",
      hex"6c1d56f31b2de4bfc6aaea28396b333102b1f600da9c6d6149e96ca43f1102b1",
      hex"7a1de137cbafb5c70405455c49c5104ca3057a1f1243e6563bb9245c9c88c191",
      hex"7d037ceb2ee0dc03e82f17be7935d238b35d1deabf953a892a4507bfbeeb3ba4",
      hex"a5e899dddb28776ea9ddac0a502316d53a4a3fca607c72f66c470e0412e34086",
      hex"b4112b8f900a7ca0c8b0e7c4dfad35c6be5f6be46b3458974988e1cdb2fa61b8",
      hex"bafd65e3c7f3f9fdfdc1ddb026131b278c3be1af90a4a6ffa78c4658f9ec0c85",
      hex"de0411a1e97484a2804ff1dbde260ac19de841bebad1880c782941aca883b4e9",
      hex"f0a130a84912d03c1d284974f563c5949ac13f8342b8112edff52971599e6a45",
      hex"f320832a9d2e2452af63154bc687493484a0e7745ebd3aaf9ca19eb80834ad60"
    ).map(ByteVector32(_))
    assert(tx1.txIn.map(_.outPoint.txid) == expected)
  }

  test("reference test #2") {
    val tx = Transaction.read("")
    assert(tx.txid == ByteVector32(hex"28204cad1d7fc1d199e8ef4fa22f182de6258a3eaafe1bbe56ebdcacd3069a5f"))

    val tx1 = LexicographicalOrdering.sort(tx)
    assert(tx1.txIn.map(_.outPoint) == Seq(
      OutPoint(ByteVector32(hex"35288d269cee1941eaebb2ea85e32b42cdb2b04284a56d8b14dcc3f5c65d6055".reverse), 0),
      OutPoint(ByteVector32(hex"35288d269cee1941eaebb2ea85e32b42cdb2b04284a56d8b14dcc3f5c65d6055".reverse), 1)
    ))
    assert(tx1.txOut == Seq(
      TxOut(100000000 sat, hex"41046a0765b5865641ce08dd39690aade26dfbf5511430ca428a3089261361cef170e3929a68aee3d8d4848b0c5111b0a37b82b86ad559fd2a745b44d8e8d9dfdc0cac"),
      TxOut(2400000000L sat, hex"41044a656f065871a353f216ca26cef8dde2f03e8c16202d2e8ad769f02032cb86a5eb5e56842e92e19141d60a01928f8dd2c875a390f67c1f6c94cfc617c0ea45afac")
    ))
  }

  test("sort bytes") {
    assert(!LexicographicalOrdering.isLessThan(ByteVector.empty, ByteVector.empty))
    assert(!LexicographicalOrdering.isLessThan(hex"aaaa", hex"aaaa"))
    assert(LexicographicalOrdering.isLessThan(ByteVector.empty, hex"aa"))
    assert(LexicographicalOrdering.isLessThan(hex"aaaa", hex"aaaa00"))
    assert(!LexicographicalOrdering.isLessThan(hex"aaaa00", hex"aaaa"))
  }

  test("sort outputs with similar publicKeyScripts") {
    val txOut1 = TxOut(14999 sat, hex"12345678")
    val txOut2 = TxOut(15000 sat, hex"123456")
    val txOut3 = TxOut(15000 sat, hex"12345678")
    val tx = LexicographicalOrdering.sort(Transaction(2, Nil, Seq(txOut3, txOut2, txOut1), 0))
    assert(tx === Transaction(2, Nil, Seq(txOut1, txOut2, txOut3), 0))
  }
}
